package javabasico;

public class Actividad2 {

    public static void main(String[] args) {
        // TODO Auto-generated method stub
        int a = 66;
        long b = 10;
        char c ='A';
        double d = 1.8;
        //a
        long along = a;
        char achar = (char) a;
        //b
        double bdouble = b;
        int bint = (int) b;
        //c
        int cint = c;

        //d
        float dfloat = (float) d;
        int dint =(int) d;

        //pantalla
        System.out.println("resultado  conversion");
        System.out.println("a=" + along + " y " + achar);
        System.out.println("b=" + bdouble + " y "+ bint );
        System.out.println("c="+ cint);
        System.out.println("d="+dfloat+" y "+dint);













    }

}
